import { Injectable } from '@angular/core';
import { AppStateItem } from '@common/context/state/app-state.service';
import { BehaviorSubject } from 'rxjs';
import { MenuItem } from '../model/menu-item.model';

@Injectable({
  providedIn: 'root',
})
export class LayoutStateService {
  public readonly menu = buildLayoutStateItem<MenuItem[]>([]);
}

function buildLayoutStateItem<T>(def: T): AppStateItem<T> {
  const base = new BehaviorSubject<T>(def);
  const instance = base as any;
  instance.set = (value: T) => {
    base.next(value);
  };
  return instance;
}
