/* @autogenerated */
import { Component } from '@angular/core';
import { IdentityService } from '@common/context/identity/identity.service';
import { Subscription, take } from 'rxjs';
import { AuthorizationAccessService } from './authorization-access.service';

@Component({
  selector: 'authorization-access',
  standalone: true,
  template: '<h1>Home</h1>',
})
export class AuthorizationAccessComponent {
  private readonly susbcription = new Subscription();

  constructor(
    private readonly identity: IdentityService,
    private readonly ser: AuthorizationAccessService,
  ) {
    this.susbcription.add(
      this.identity
        .bootstrap('main')
        .pipe(take(1))
        .subscribe((_) => {
          this.ser.loadLastRoute();
        }),
    );
  }
}
