/* @autogenerated */
import { EnvironmentProviders, provideAppInitializer, makeEnvironmentProviders, inject } from '@angular/core';
import { Route, RouterModule } from '@angular/router';
import { importProvidersFrom } from '@angular/core';
import { AuthorizationAccessComponent } from './authorization-access.component';
import { AuthorizationAccessService } from './authorization-access.service';
import { UnauthorizedComponent } from './unauthorized.component';

export function provideAuthorizationAccess(): EnvironmentProviders {
  const routes: Route[] = [
    {
      path: 'login-redirect',
      component: AuthorizationAccessComponent,
    },
    {
      path: 'unauthorized',
      component: UnauthorizedComponent,
    },
  ];

  return makeEnvironmentProviders([
    importProvidersFrom(RouterModule.forChild(routes)),
    provideAppInitializer(() => {
      // No need to act, just instance
      const _service = inject(AuthorizationAccessService);
    }),
  ]);
}
