/* @autogenerated */
import { Directive, Input, TemplateRef } from '@angular/core';

@Directive({
  selector: 'ng-template[localRoute]',
})
export class LocalRouteDirective {
  @Input('localRoute') on: string | undefined;
  @Input() initial: string | boolean = 'false';
  @Input() fallback: string | boolean = 'false';

  constructor(public template: TemplateRef<any>) {}

  isFallback() {
    return this.fallback === 'true' || this.fallback === true;
  }

  isInitial() {
    return this.initial === 'true' || this.initial === true;
  }
}
