/* @autogenerated */
import {
  ConfigTemplateFilter,
  ConfigTemplateProjection,
} from 'app/features/infrastructure/config-template/model/config-template.model';
import { ConfigTemplateRepository } from 'app/features/infrastructure/config-template/repository/config-template-repository.service';
import { FormControl } from '@angular/forms';
import { Shift } from '@common/data/repository/repository.model';
import { input, effect, Component } from '@angular/core';

@Component({
  selector: 'config-template-view-list',
  templateUrl: './config-template-view-list.component.html',
  styleUrls: ['./config-template-view-list.component.scss'],
  standalone: false,
})
export class ConfigTemplateViewListComponent {
  filters = input<ConfigTemplateFilter>();
  actionName = input<string>('config-template');
  fixed = input<{ [key: string]: any }>();
  hidden = input<{ [key: string]: any }>();
  search = new FormControl<any>(undefined);
  list: Shift<ConfigTemplateProjection[], ConfigTemplateFilter>;

  constructor(repository: ConfigTemplateRepository) {
    this.list = repository.projections([]);
    effect(() => {
      setTimeout(() => this.makeSearch());
    });
  }
  makeSearch() {
    const result = { limit: 10 } as any;
    const filter = this.search.value;
    const external = this.filters();
    if (external?.search) {
      result.search = external.search;
    }
    if (external?.name) {
      result.name = external.name;
    }
    if (filter?.search) {
      result.search = filter.search;
    }
    if (filter?.name) {
      result.name = filter.name;
    }
    if (filter?.order) {
      result.order = filter.order;
    }
    this.list.fetch(result);
  }

  next() {
    this.list.next();
  }

  reload() {
    this.list.reset();
  }

  prev() {
    console.log('PREV');
  }
}
