/* @autogenerated */
import { FormControl } from '@angular/forms';
import { VendorProjection, VendorFilter } from 'app/features/infrastructure/vendor/model/vendor.model';
import { VendorRepository } from 'app/features/infrastructure/vendor/repository/vendor-repository.service';
import { Shift } from '@common/data/repository/repository.model';
import { input, effect, Component } from '@angular/core';

@Component({
  selector: 'vendor-view-list',
  templateUrl: './vendor-view-list.component.html',
  styleUrls: ['./vendor-view-list.component.scss'],
  standalone: false,
})
export class VendorViewListComponent {
  filters = input<VendorFilter>();
  actionName = input<string>('vendor');
  fixed = input<{ [key: string]: any }>();
  hidden = input<{ [key: string]: any }>();
  search = new FormControl<any>(undefined);
  list: Shift<VendorProjection[], VendorFilter>;

  constructor(repository: VendorRepository) {
    this.list = repository.projections([]);
    effect(() => {
      setTimeout(() => this.makeSearch());
    });
  }
  makeSearch() {
    const result = { limit: 10 } as any;
    const filter = this.search.value;
    const external = this.filters();
    if (external?.search) {
      result.search = external.search;
    }
    if (external?.name) {
      result.name = external.name;
    }
    if (filter?.search) {
      result.search = filter.search;
    }
    if (filter?.name) {
      result.name = filter.name;
    }
    if (filter?.order) {
      result.order = filter.order;
    }
    this.list.fetch(result);
  }

  next() {
    this.list.next();
  }

  reload() {
    this.list.reset();
  }

  prev() {
    console.log('PREV');
  }
}
